import React, { useContext, useState } from "react";
import { CartContext } from "../CartContext";

const Checkout = () => {
  const { cart, setCart } = useContext(CartContext);

  // Customer details
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [zip, setZip] = useState("");
  
  // Payment method
  const [paymentMethod, setPaymentMethod] = useState("Credit Card");

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const handleOrder = () => {
    if (!name || !email || !phone || !address || !city || !state || !zip) {
      alert("Please fill in all customer details!");
      return;
    }
    if (!paymentMethod) {
      alert("Please select a payment method!");
      return;
    }

    alert(`Thank you ${name}!\nYour order of $${total} has been placed.\nPayment Method: ${paymentMethod}`);
    
    // Clear cart & form
    setCart([]);
    setName(""); setEmail(""); setPhone(""); setAddress(""); setCity(""); setState(""); setZip("");
    setPaymentMethod("Credit Card");
  };

  const increaseQty = (id) => {
    setCart(cart.map(item => item.id === id ? { ...item, qty: item.qty + 1 } : item));
  };

  const decreaseQty = (id) => {
    setCart(cart.map(item => item.id === id ? { ...item, qty: item.qty > 1 ? item.qty - 1 : 1 } : item));
  };

  return (
    <div className="checkout-wrapper">

      {/* Left: Customer Details */}
      <div className="checkout-left">
        <h2>Customer Details</h2>
        <input type="text" placeholder="Full Name" value={name} onChange={e => setName(e.target.value)} />
        <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
        <input type="text" placeholder="Phone Number" value={phone} onChange={e => setPhone(e.target.value)} />
        <input type="text" placeholder="Address" value={address} onChange={e => setAddress(e.target.value)} />
        <input type="text" placeholder="City" value={city} onChange={e => setCity(e.target.value)} />
        <input type="text" placeholder="State" value={state} onChange={e => setState(e.target.value)} />
        <input type="text" placeholder="Zip / Postal Code" value={zip} onChange={e => setZip(e.target.value)} />

        {/* Payment Method */}
        <h3 style={{ marginTop: "20px", color: "#4e342e" }}>Payment Method</h3>
        <select value={paymentMethod} onChange={e => setPaymentMethod(e.target.value)} className="payment-select">
          <option value="Credit Card">Credit Card</option>
          <option value="Debit Card">Debit Card</option>
          <option value="PayPal">PayPal</option>
          <option value="Cash on Delivery">Cash on Delivery</option>
        </select>
      </div>

      {/* Right: Cart Summary */}
      <div className="checkout-right">
        <h2>Order Summary</h2>
        {cart.length === 0 ? (
          <p>Cart is empty</p>
        ) : (
          cart.map(item => (
            <div key={item.id} className="checkout-item">
              <img src={item.image} alt={item.name} />
              <div className="checkout-item-details">
                <h3>{item.name}</h3>
                <p>${item.price}</p>
                <div className="qty-controls">
                  <button onClick={() => decreaseQty(item.id)}>-</button>
                  <span>{item.qty}</span>
                  <button onClick={() => increaseQty(item.id)}>+</button>
                </div>
              </div>
            </div>
          ))
        )}
        <div className="checkout-total">
          <h3>Total: ${total}</h3>
          <button onClick={handleOrder}>Place Order</button>
        </div>
      </div>

    </div>
  );
};

export default Checkout;
