import React from 'react';
import { useCart } from '../context/CartContext';

const Cart = () => {
  const { cartItems, totalAmount, updateCartQuantity, removeItemFromCart } = useCart();

  if (cartItems.length === 0) {
    return (
      <div className="cart-container empty-cart fade-in">
        <h2 className="cart-header">Your Cart üõí</h2>
        <p className="empty-message">Your cart is empty! Time to order some food.</p>
        <button className="back-to-menu-btn">Browse Menu</button>
      </div>
    );
  }

  return (
    <div className="cart-container fade-in">
      <h2 className="cart-header">Your Order Summary</h2>
      <div className="cart-list">
        {cartItems.map((item) => (
          <div key={item.id} className="cart-item-card">
            <img src={`/images/${item.image}`} alt={item.name} className="item-image" />
            <div className="item-details">
              <h3 className="item-name">{item.name}</h3>
              <p className="item-price">Price: ${item.price.toFixed(2)}</p>
              
              {/* Quantity Controls for Cart */}
              <div className="quantity-controls cart-controls">
                <button 
                  className="qty-btn decrease" 
                  onClick={() => updateCartQuantity(item.id, -1)}
                  disabled={item.quantity <= 1}
                > - </button>
                <span className="quantity-display">{item.quantity}</span>
                <button 
                  className="qty-btn increase" 
                  onClick={() => updateCartQuantity(item.id, 1)}
                > + </button>
              </div>
              
              <p className="item-subtotal">Subtotal: **${(item.price * item.quantity).toFixed(2)}**</p>
            </div>
            
            <button 
                className="remove-btn" 
                onClick={() => removeItemFromCart(item.id)}
            >
              ‚ùå Remove
            </button>
          </div>
        ))}
      </div>

      <div className="cart-summary">
        <div className="total-display">
          <span>Grand Total:</span>
          <span className="total-amount">${totalAmount.toFixed(2)}</span>
        </div>
        <button className="checkout-btn">Proceed to Checkout</button>
      </div>
    </div>
  );
};

export default Cart;
