import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";
import { Link } from "react-router-dom";


function Cart() {
  const { cartItems, increaseQty, decreaseQty, removeItem } = useContext(CartContext);

  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          {cartItems.map((item, index) => (
            <div key={index} className="cart-card">
              <img src={item.image1 || item.image} alt={item.name} className="cart-img"/>
              <div className="cart-info">
                <h3>{item.name}</h3>
                <p>Price: ${item.price}</p>

                <div className="quantity-controls">
                  <button onClick={() => decreaseQty(item.name)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => increaseQty(item.name)}>+</button>
                </div>

                <p>Subtotal: ${item.price * item.quantity}</p>
                <button className="remove-btn" onClick={() => removeItem(item.name)}>Remove</button>
              </div>
            </div>
          ))}

          <h2 className="total">Total: ${total}</h2>

          <Link to="/checkout">
            <button className="checkout-btn">Proceed to Checkout</button>
          </Link>
        </>
      )}
    </div>
  );
}

export default Cart;
