import React from "react";

const Menu = ({ cart, setCart }) => {
  const products = [
    { id: 1, name: "Burger", price: 5, image: "https://i.pinimg.com/1200x/e2/63/e5/e263e50bc39528becbd777a271e297af.jpg" },
    { id: 2, name: "Pizza", price: 8, image: "https://i.pinimg.com/1200x/40/7e/34/407e34d39fd1f0c2b3cc3c8dc4eb5219.jpg" },
    { id: 3, name: "Fries", price: 3, image: "https://i.pinimg.com/736x/6e/7b/5e/6e7b5e91357a7ce785a75d3449c1ded5.jpg" },
    { id: 10, name: "wrap", price: 5, image: "https://i.pinimg.com/736x/1f/c8/99/1fc899f5ae2e4fddc80b6c1c81d93842.jpg" },
    { id: 11, name: "shawarma", price: 8, image: "https://i.pinimg.com/736x/8b/24/8f/8b248fd2fb6153dac9616f966ea66294.jpg" },
    { id: 12, name: "stake", price: 3, image: "https://i.pinimg.com/1200x/3c/a9/9a/3ca99a55d718100b7dc154093d704410.jpg" },
{ id: 4, name: "Stake Burger", price: 5, image: "data:image/jpeg;base64," },
    { id: 5, name: "Crown Crust pizza", price: 8, image: "https://i.pinimg.com/1200x/40/7e/34/407e34d39fd1f0c2b3cc3c8dc4eb5219.jpg" },
    { id: 6, name: "zinger burger", price: 3, image: "https://i.pinimg.com/736x/6e/7b/5e/6e7b5e91357a7ce785a75d3449c1ded5.jpg" },
    { id: 7, name: "Mighty zinger", price: 5, image: "https://i.pinimg.com/736x/1f/c8/99/1fc899f5ae2e4fddc80b6c1c81d93842.jpg" },
    { id: 8, name: "Lava Burger", price: 8, image: "https://i.pinimg.com/736x/8b/24/8f/8b248fd2fb6153dac9616f966ea66294.jpg" },
    { id: 9, name: "zinger Shwarma", price: 3, image: "https://i.pinimg.com/1200x/3c/a9/9a/3ca99a55d718100b7dc154093d704410.jpg" },
 ];
  const addToCart = (product) => {
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      setCart(cart.map(item => item.id === product.id ? { ...item, qty: item.qty + 1 } : item));
    } else {
      setCart([...cart, { ...product, qty: 1 }]);
    }
  };

  return (
    <div className="menu">
      <h1>Our Menu</h1>
      <div className="menu-items">
        {products.map(product => (
          <div key={product.id} className="menu-item">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>${product.price}</p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
