import React, { useContext } from "react";
import { CartContext } from "../CartContext";

const products = [
  { id: 1, name: "Burger", price: 5, image: "https://source.unsplash.com/200x200/?burger" },
  { id: 2, name: "Pizza", price: 8, image: "https://source.unsplash.com/200x200/?pizza" },
  { id: 3, name: "Coffee", price: 3, image: "https://source.unsplash.com/200x200/?coffee" },
];

const Menu = () => {
  const { cart, setCart } = useContext(CartContext);

  const addToCart = (product) => {
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      setCart(cart.map(item => item.id === product.id ? { ...item, qty: item.qty + 1 } : item));
    } else {
      setCart([...cart, { ...product, qty: 1 }]);
    }
  };

  return (
    <div className="menu-container">
      {products.map((item) => (
        <div key={item.id} className="menu-item">
          <img src={item.image} alt={item.name} />
          <h3>{item.name}</h3>
          <p>${item.price}</p>
          <button onClick={() => addToCart(item)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
};

export default Menu;
