import React, { useState } from "react";
import { createClient } from "@supabase/supabase-js";
import jsPDF from "jspdf";

const supabaseUrl = "https://<your-project-ref>.supabase.co";
const supabaseKey = "<your-anon-key>";
const supabase = createClient(supabaseUrl, supabaseKey);

const OrderForm = () => {
  const [customer_name, setName] = useState("");
  const [customer_email, setEmail] = useState("");
  const [order_details, setDetails] = useState("");
  const [total_amount, setAmount] = useState("");

  const blobToBase64 = (blob) => {
    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result.split(",")[1]);
      reader.readAsDataURL(blob);
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const invoice_number = `INV-${Date.now()}`;

    const orderData = { customer_name, customer_email, order_details, total_amount, invoice_number };

    // Save order in Supabase
    await supabase.from("orders").insert([orderData]);

    // Generate PDF invoice
    const doc = new jsPDF();
    doc.text(`Invoice #${invoice_number}`, 10, 10);
    doc.text(`Customer: ${customer_name}`, 10, 20);
    doc.text(`Email: ${customer_email}`, 10, 30);
    doc.text(`Order Details: ${order_details}`, 10, 40);
    doc.text(`Total Amount: $${total_amount}`, 10, 50);

    const pdfBlob = doc.output("blob");
    const pdfBase64 = await blobToBase64(pdfBlob);

    // Call Supabase Edge Function → send email
    const response = await fetch('https://<your-project-ref>.functions.supabase.co/send_order_email', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...orderData, pdfBase64 })
    });

    const result = await response.json();
    if(result.success){
      alert("Order confirmed! Email with invoice sent.");
    } else {
      alert("Email failed: " + result.error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Name" value={customer_name} onChange={e => setName(e.target.value)} required />
      <input type="email" placeholder="Email" value={customer_email} onChange={e => setEmail(e.target.value)} required />
      <textarea placeholder="Order Details" value={order_details} onChange={e => setDetails(e.target.value)} required />
      <input type="number" placeholder="Total Amount" value={total_amount} onChange={e => setAmount(e.target.value)} required />
      <button type="submit">Place Order</button>
    </form>
  );
};

export default OrderForm;
